<span class="line"><span class="token keyword">const</span> url <span class="token operator">=</span> <span class="token function">buildOrderDetailUrl</span><span class="token punctuation">(</span><span class="token string">'12345'</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// '/api/order/12345'</span></span>
