<span class="line"><span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">'@shixinde/apifox-swagger/types'</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line"><span class="token comment">// 通用 API 调用函数</span></span>
<span class="line"><span class="token keyword">async</span> <span class="token keyword">function</span> <span class="token generic-function"><span class="token function">apiCall</span><span class="token generic class-name"><span class="token operator">&lt;</span></span>
<span class="line">  <span class="token constant">U</span> <span class="token keyword">extends</span> <span class="token keyword">keyof</span> paths<span class="token punctuation">,</span></span>
<span class="line">  <span class="token constant">M</span> <span class="token keyword">extends</span> InferMethodFromPaths<span class="token operator">&lt;</span>paths<span class="token punctuation">,</span> <span class="token constant">U</span><span class="token operator">></span></span>
<span class="line"><span class="token operator">></span></span></span><span class="token punctuation">(</span></span>
